import { useState } from 'react'



function rgbToHex(r, g, b) {

    return ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
}



function HEX2RGB (hex) {
    "use strict";
    if (hex.charAt(0) === '#') {
        hex = hex.substr(1);
    }
    if ((hex.length < 2) || (hex.length > 6)) {
        return false;
    }
    var values = hex.split(''),
        r,
        g,
        b;

    if (hex.length === 2) {
        r = parseInt(values[0].toString() + values[1].toString(), 16);
        g = r;
        b = r;
    } else if (hex.length === 3) {
        r = parseInt(values[0].toString() + values[0].toString(), 16);
        g = parseInt(values[1].toString() + values[1].toString(), 16);
        b = parseInt(values[2].toString() + values[2].toString(), 16);
    } else if (hex.length === 6) {
        r = parseInt(values[0].toString() + values[1].toString(), 16);
        g = parseInt(values[2].toString() + values[3].toString(), 16);
        b = parseInt(values[4].toString() + values[5].toString(), 16);
    } else {
        return false;
    }
    return [r, g, b];
}


export default function Index() {
    const [rValue, setRValue] = useState(0)
    const [gValue, setGValue] = useState(0)
    const [bValue, setBValue] = useState(0)
    const [theHex, setTheHex] = useState("FFFFFF")
    const [theRGB, setTheRGB] = useState()
    const [theColor, setTheColor] = useState()

    const setDefault = () => {
        // setRValue(null)
        // setGValue(null)
        // setBValue(null)
        // setTheHex(null)
        setTheColor({background: "#FFFFFF"})
        // alert("empty")
    }

    const setRGB = (str) => {
        const result = HEX2RGB(str)

        setRValue(result[0])
        setGValue(result[1])
        setBValue(result[2])

        setTheHex(str)
        setTheColor({background: "#" + str})

    }

    const setHEX = (r,g,b) => {
        let red = r
        let green = g
        let blue = b

        const result = rgbToHex(red,green,blue)

        setRValue(red)
        setGValue(green)
        setBValue(blue)

        setTheHex(result)
        setTheColor({background: "#" + result})

    }

    return (
      <div style={theColor}>
        <p>Hello Next.js</p>

        <p>Red </p> {rValue}
        <input 
        type="text"
        placeholder = "Enter Red value"
        value = {rValue}
        maxLength = "3"
        onChange = {(e) => {
            if (e.target.value) {
                setHEX(e.target.value, gValue, bValue)
            } else {
                setRValue(null)
                setDefault()
            }
        }
        }
        />

        <p>Green </p> {gValue}
        <input 
        type="text"
        placeholder = "Enter Green value"
        value = {gValue}
        maxLength = "3"
        onChange = {(e) => {
            if (e.target.value) {
                setHEX(rValue, e.target.value, bValue)
            } else {
                setGValue(null)
                setDefault()
            }
        }
        }
        />

        <p>Blue </p> {bValue}
        <input 
        type="text"
        placeholder = "Enter Blue value"
        value = {bValue}
        maxLength = "3"
        onChange = {(e) => {
            if (e.target.value) {
                setHEX(rValue, gValue, e.target.value)
            } else {
                setBValue(null)
                setDefault()
            }
        }
        }
        />


        <br/>
        <br/>

        <p>Hex Value </p>{theHex}
        <input 
        type="text"
        placeholder = "Enter value"
        maxLength = "6"
        value = {theHex}
        onChange = {(e) => {
            if (e.target.value) {
                setRGB(e.target.value)
            } else {
                setTheHex(null)
                setDefault()
            }

        }
        }
        />


      </div>
    );
  }
