import { useState } from 'react'



const linkStyle = {
    background: "#F3F3F3", 
    height: "100px",
    width: "100px"
  };


const RGB_HEX = /^#?(?:([\da-f]{3})[\da-f]?|([\da-f]{6})(?:[\da-f]{2})?)$/i;

const hex2RGB = str => {
  const [ , short, long ] = String(str).match(RGB_HEX) || [];

  if (long) {
    const value = Number.parseInt(long, 16);
    return [ value >> 16, value >> 8 & 0xFF, value & 0xFF ];
  } else if (short) {
    return Array.from(short, s => Number.parseInt(s, 16)).map(n => (n << 4) | n);
  }
};

const setRBG = (str) => {
    hex2RGB
}



export default function Index() {
    const [rValue, setRValue] = useState(200)
    const [gValue, setGValue] = useState(200)
    const [bValue, setBValue] = useState(200)
    return (
      <div style={linkStyle}>
        <p>Hello Next.js</p>

        <p>Red</p>
        {rValue}

        <p>Green</p>
        {gValue}

        <p>Blue</p>
        {bValue}

        <input 
        type="text"
        placeholder = "Enter R value"
        onChange = {(e) => hex2RGB(e.target.value)}
        />


      </div>
    );
  }
