import { useState } from 'react'






const hex = /^#?(?:([\da-f]{3})[\da-f]?|([\da-f]{6})(?:[\da-f]{2})?)$/i;


function HEX2RGB(hex) {
    // Expand shorthand form (e.g. "03F") to full form (e.g. "0033FF")
    var shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    hex = hex.replace(shorthandRegex, function(m, r, g, b) {
      return r + r + g + g + b + b;
    });

    var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result ? {
      r: parseInt(result.r, 16),
      g: parseInt(result.g, 16),
      b: parseInt(result.b, 16)
    } : null;
  }




export default function Index() {
    const linkStyle = {
        background: "#F3F3F3", 
        height: "100px",
        width: "100px"
      };

    const [rValue, setRValue] = useState()
    const [gValue, setGValue] = useState()
    const [bValue, setBValue] = useState()

    const setRBG = (str) => {
        const result = HEX2RGB(str)
        setRValue(result[0]);
        setGValue(result[1]);
        setBValue(result[2]);


    }

    return (
      <div style={linkStyle}>
        <p>Hello Next.js</p>

        <p>Red</p>
        {rValue}

        <p>Green</p>
        {gValue}

        <p>Blue</p>
        {bValue}

        <input 
        type="text"
        placeholder = "Enter R value"
        onChange = {(e) => setRBG(e.target.value)}
        />


      </div>
    );
  }
