import { useState } from 'react'






const RGB_HEX = /^#?(?:([\da-f]{3})[\da-f]?|([\da-f]{6})(?:[\da-f]{2})?)$/i;

function rgbToHex(r, g, b) {
    return "#" + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
}


function HEX2RGB (hex) {
    "use strict";
    if (hex.charAt(0) === '#') {
        hex = hex.substr(1);
    }
    if ((hex.length < 2) || (hex.length > 6)) {
        return false;
    }
    var values = hex.split(''),
        r,
        g,
        b;

    if (hex.length === 2) {
        r = parseInt(values[0].toString() + values[1].toString(), 16);
        g = r;
        b = r;
    } else if (hex.length === 3) {
        r = parseInt(values[0].toString() + values[0].toString(), 16);
        g = parseInt(values[1].toString() + values[1].toString(), 16);
        b = parseInt(values[2].toString() + values[2].toString(), 16);
    } else if (hex.length === 6) {
        r = parseInt(values[0].toString() + values[1].toString(), 16);
        g = parseInt(values[2].toString() + values[3].toString(), 16);
        b = parseInt(values[4].toString() + values[5].toString(), 16);
    } else {
        return false;
    }
    return [r, g, b];
}





export default function Index() {
    const [rValue, setRValue] = useState()
    const [gValue, setGValue] = useState()
    const [bValue, setBValue] = useState()
    const [theHex, setTheHex] = useState()
    const [theRGB, setTheRGB] = useState([])
    const [theColor, setTheColor] = useState([])

    const setRBG = (str) => {
        const result = HEX2RGB(str)
        setTheRGB([result[0], result[1], result[2]])
        setTheHex(str)
        setTheColor({background: "#" + str})

    }

    const setHEX = (r,g,b) => {
        const result = rgbToHex(r,g,b)
        setTheRGB(r,g,b)
        setTheHex(result)
        setTheColor({background: "#" + result})

    }

    return (
      <div style={theColor}>
        <p>Hello Next.js</p>

        <p>Red</p>
        <input 
        type="text"
        placeholder = "Enter Red value"
        value = {theRGB[0]}
        onChange = {(e) => {
            setHEX(e.target.value, theRGB[1], theRGB[2])
        }
        }
        />

        <p>Green</p>
        <input 
        type="text"
        placeholder = "Enter Green value"
        value = {theRGB[1]}
        onChange = {(e) => {
            setHEX(theRGB[0], e.target.value, theRGB[2])
        }
        }
        />

        <p>Blue</p>
        <input 
        type="text"
        placeholder = "Enter Blue value"
        value = {theRGB[2]}
        onChange = {(e) => {
            setHEX(theRGB[0], theRGB[1], e.target.value)
        }
        }
        />


        <br/>

        <input 
        type="text"
        placeholder = "Enter value"
        maxLength = "6"
        value = {theHex}
        onChange = {(e) => {
            setRBG(e.target.value)
        }
        }
        />


      </div>
    );
  }
